<?php add_action('init', 'psector_custom_post_types');function psector_custom_post_types() {	// Product features		$labels = array(			'name' => _x('Business Services', 'post type general name'),			'singular_name' => _x('Business Service', 'post type singular name'),			'add_new' => _x('Add Business Service', 'Support posts'),			'add_new_item' => __('Add New Business Service'),			'edit_item' => __('Edit Business Service'),			'new_item' => __('New Business Service'),			'view_item' => __('View Business Service'),			'search_items' => __('Search Business Service'),			'not_found' =>  __('No Business Service found'),			'not_found_in_trash' => __('No Business Service posts found in Trash'),			'parent_item_colon' => ''		);		register_post_type(			'psector',			array(				'labels' => $labels,				'public' => true,				'show_ui' => true,				'hierarchical' => true,								'rewrite' => true,				'exclude_from_search' => false,				'show_in_nav_menus' => true,				'supports' => array(					'title',					//'editor',					'page-attributes',					//'thumbnail',					//'excerpt',					//'comments',					//'author'				)			)		);}//add_action( 'init', 'build_psector_taxonomies', 0 );     function build_psector_taxonomies() {   	register_taxonomy( 'psector-category', 'psector', 						array(	'hierarchical' => true, 								'label' => 'P sector Category', 								'query_var' => true, 								'rewrite' => true								) 					); 						register_taxonomy( 'content-type', 'psector', 						array(	'hierarchical' => true, 								'label' => 'Content Type', 								'query_var' => true, 								'rewrite' => true								) 					);  					} //add_filter('manage_edit-psector_columns', 'psector_list_columns');// function psector_list_columns($columns) {// 	if (!$columns['psector_category']) {// 		$new_columns = array();// 		foreach($columns as $column_key => $column_val) {// 			$new_columns[$column_