<?php global $head;add_action('init', 'sector_custom_post_types');function sector_custom_post_types() {	// Product features		$labels = array(			'name' => _x('Sectors', 'post type general name'),			'singular_name' => _x('Sector', 'post type singular name'),			'add_new' => _x('Add Sector', 'Support posts'),			'add_new_item' => __('Add New Sector'),			'edit_item' => __('Edit Sector'),			'new_item' => __('New Sector'),			'view_item' => __('View Sector'),			'search_items' => __('Search Sector'),			'not_found' =>  __('No Sector found'),			'not_found_in_trash' => __('No Sector posts found in Trash'),			'parent_item_colon' => ''		);		register_post_type(			'sector',			array(				'labels' => $labels,				'public' => true,				'show_ui' => true,				'hierarchical' => true,					'has_archive' => false,							'rewrite' => true,				'exclude_from_search' => false,				'show_in_nav_menus' => true,				'show_in_rest' => true,				'supports' => array(					'title',					'editor',					'page-attributes',					'thumbnail',					//'excerpt',					//'comments',					//'author'				)			)		);}//add_action( 'init', 'build_sector_taxonomies', 0 );     function build_sector_taxonomies() {   	register_taxonomy( 'sector-category', 'sector', 						array(	'hierarchical' => true, 								'label' => 'Sector Category', 								'query_var' => true, 								'rewrite' => true								) 					); 						register_taxonomy( 'content-type', 'sector', 						array(	'hierarchical' => true, 								'label' => 'Content Type', 								'query_var' => true, 								'rewrite' => true								) 					);  					} ?>